mapper.select:
SELECT * FROM Information 

   WHERE ${key1}=#{value1} 


   AND ${key2}=#{value2} 


   AND ${key3}=#{value3}


   AND ${key4} != #{value4}


   ORDER BY ${orderBy}


   ORDER BY resSeq DESC;

SELECT * FROM Timetable 

   WHERE ${key1} = #{value1}


   AND ${key2} = #{value2}


   AND ${key3} = #{value3}

SELECT DISTINCT ${column1}

   , ${column2}

FROM Showing;

mapper.update:
UPDATE Article 
SET article_type = #{article_type}, 
title = #{title}, 
content = #{content},
file = #{file}
WHERE seq = ${seq}

UPDATE Article 
SET answer = 'O'
WHERE seq = ${seq}

UPDATE Customer
id=#{id}, pw=#{pw}, name=#{name}, birth=#{birth}, phone=#{phone}, gender=#{gender}, address=#{address}, email=#{email}, point=#{point},
WHERE id=#{id};

UPDATE Notice
content=#{content}, file=#{file}, reg_date=#{reg_date}, title=#{title},
WHERE seq=#{seq};

Update Showing
show_date=#{showDate}, start_time=#{startTime}, end_time=#{endTime},
WHERE seq=#{seq};

mapper.select:
SELECT COUNT(*) FROM ${group}

WHERE ${key1} like '%${value1}%'


WHERE ${key2} like '%${value2}%' OR ${key3} like '%${value3}%'

SELECT COUNT(*) FROM ${group} 

   WHERE ${key} LIKE '%${value}%'


   AND ${key2} LIKE '%${value2}%'


   AND ${key3} != #{value3}


   AND ${key4} LIKE '%${value4}%'


   AND ${key5} LIKE '%${value5}%'
